// Transform for invoice rows
const out = $input.all().map(it => {
  const d = it.json || {};

  // build short invoice summary for embedding
  const summary = [
    `Invoice: ${d['Invoice No.'] || d.invoice_number || 'Unknown'}`,
    `Date: ${d['Dated'] || d.invoice_date || 'Unknown'}`,
    `Supplier: ${d['Supplier Name'] || d.supplier_name || 'Unknown'}`,
    `Total: ${d['Total (Grand Total)'] || d.total_amount || 'Unknown'}`,
    `Taxable: ${d['Taxable Value'] || d.taxable_value || 'Unknown'}`,
    `HSN: ${Array.isArray(d['HSN/SAC']) ? d['HSN/SAC'].slice(0,3).join(', ') : (d['HSN/SAC'] || '')}`
  ].filter(Boolean).join(' • ');

  // metadata keys (normalize to simple keys)
  const metadata = {
    unique_id: ((d['Invoice No.'] || d.invoice_number || '') + '_' + (d['Supplier GSTIN/UIN'] || d.supplier_gstin || '')).replace(/[^a-zA-Z0-9-_]/g,'_').toLowerCase(),
    invoice_number: d['Invoice No.'] || d.invoice_number || null,
    invoice_date: d['Dated'] || d.invoice_date || null,
    supplier_name: d['Supplier Name'] || d.supplier_name || null,
    supplier_gstin: d['Supplier GSTIN/UIN'] || d.supplier_gstin || null,
    hsn_sac: Array.isArray(d['HSN/SAC']) ? d['HSN/SAC'] : (d['HSN/SAC'] ? [d['HSN/SAC']] : []),
    quantity_total: d['Quantity (Total)'] || d.quantity_total || null,
    taxable_value: d['Taxable Value'] ? Number(String(d['Taxable Value']).replace(/[,₹\s]/g,'')) : null,
    cgst_amount: d['CGST Amount'] ? Number(String(d['CGST Amount']).replace(/[,₹\s]/g,'')) : null,
    sgst_amount: d['SGST/UTGST Amount'] ? Number(String(d['SGST/UTGST Amount']).replace(/[,₹\s]/g,'')) : null,
    total_amount: d['Total (Grand Total)'] ? Number(String(d['Total (Grand Total)']).replace(/[,₹\s]/g,'')) : null,
    raw_text: d.raw_text || d.text || JSON.stringify(d).slice(0,2000),
    row_number: d.row_number || null,
    data_type: 'invoice',
    source: d.source || 'sheet_import',
    parse_confidence: d.parse_confidence || 0.8 // you can compute this earlier
  };

  return {
    pageContent: summary + '\n\n' + (metadata.raw_text || ''),
    metadata,
    original_data: d
  };
}).filter(Boolean);

return out;

const input = $input.all();
const processed = [];

for (const it of input) {
  const meta = it.json.metadata || it.json; // if Transform already returned metadata
  if (!meta) continue;

  const uniqueId = meta.unique_id || ((meta.invoice_number || '') + '_' + (meta.supplier_gstin || '')).replace(/[^a-zA-Z0-9]/g,'_').toLowerCase();
  meta.unique_id = uniqueId;
  meta.is_update = !!meta.invoice_number && !!meta.supplier_gstin; // heuristics; you can check DB for existence later

  const pageContent = it.json.pageContent || JSON.stringify(it.json.original_data || meta).slice(0,2000);

  processed.push({
    pageContent,
    metadata: meta,
    original_data: it.json.original_data || {}
  });
}

return processed;

let sentence = `Invoice Number ${$input.first().json['Invoice Number']}, dated ${$input.first().json['Invoice Date']}, from Supplier Name ${$input.first().json['Supplier Name']}, lists Total Amount ${$input.first().json['Total Amount']}, and Taxable Value ${$input.first().json['Taxable Value']}, with CGST Amount ${$input.first().json['CGST Amount']} and Quantity ${$input.first().json['Quantity']} view at Invoice_Link ${$input.first().json['Invoice_Link']} — HSN/SAC Code ${$input.first().json['HSN/SAC Code']}, Supplier GSTIN ${$input.first().json['Supplier GSTIN']}`;

return {
  json: {
    structured_text: sentence
  }
};
